import React, { useEffect, useState } from 'react';
import Loading from './Components/Loading';
import Tours from './Components/Tours';

const App = () => {
  const [loading, setloading] = useState('');
  const [tours, setTours] = useState([]);

  // 1. fetching data  url = https://course-api.com/react-tours-project
  // 2.sending tours as props to Tourse components

  const fetchTourse = async () => {
    setloading(true);
    try {
      let res = await fetch(' https://course-api.com/react-tours-project');
      let data = await res.json();
      setloading(false);
      setTours(tours);
      console.log('data:', data);
    } catch (err) {
      setloading(false);
      console.log('err:', err);
    }
  };

  useEffect(() => {
    fetchTourse();
  }, []);
  if (loading) {
    return (
      <>
        <Loading />
      </>
    );
  }
  return (
    <>
    {data.map((elem)=>{
   <div>
     <img src={elem.image} alt="" />
     <p>{}</p>
   </div>
    })}
    </>
  );
};

export default App;
